import React from 'react';
import logo from './logo.svg';
import Main from './Main.js'
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";
import axios from 'axios';


class Login extends React.Component  {

state = {
  'name': null,
  'email': null,
  'success': null,
  'pass': null,

}

createUser(e){
  e.preventDefault();
  var data = {name: this.state.name, email: this.state.email, pass: this.state.pass};
  axios.post('http://localhost:3001/createUser', data)
  .then( (res) => {
    console.log(res.data);
    this.setState({'success': res.data.success});
  })
  .catch( (err) => {
    console.log(err);
  })
}

login(e){
  e.preventDefault();
  var data = {email: this.state.email, pass: this.state.pass};
  axios.post('http://localhost:3001/login', data)
  .then( (res) => {
    console.log(res.data);
    this.setState({'success': res.data.success});
  })
  .catch( (err) => {
    console.log(err);
  })
}

setName(e){
  this.setState({name: e.target.value})
}

setEmail(e) {
  this.setState({email: e.target.value})
}

setPass(e) {
  this.setState({pass: e.target.value})
}

//<Dropdown onChange={} options={['History', 'Friends']}/>


  render(){
  return( 
       <div className="App">
      <h1 align="left">fooD</h1>

    <h2 align="center">Login</h2>
    <form onSubmit={this.login.bind(this)}>
    <label>Email address</label>
    <input type="text" value={this.state.email} onChange={this.setEmail.bind(this)} />
    <label>Password</label>
    <input type="text" value={this.state.pass} onChange={this.setPass.bind(this)} />
    <input type="submit" value="Submit" />
    </form>
    {this.state.success && 
    <Router>
      <Switch>
      <Route path="/main">
      <Main />
    </Route>
    </Switch>
    <Redirect to='/main' />

    </Router>

  }

  {this.state.success == false &&
    <div>
    <h2>User not found!</h2>
    <h2 align="center">Create account</h2>
    <form onSubmit={this.createUser.bind(this)}>
    <label>Name</label>
    <input type="text" value={this.state.name} onChange={this.setName.bind(this)} />
    <label>Email address</label>
    <input type="text" value={this.state.email} onChange={this.setEmail.bind(this)} />
      <label>Password</label>
    <input type="text" value={this.state.pass} onChange={this.setPass.bind(this)} />
    <input type="submit" value="Submit" />
    </form>
    </div>

  }





  </div>
  );
}
}

export default Login;
