import React from 'react';
import logo from './logo.svg';
import './App.css';
import {Map, Marker, Popup, TileLayer} from 'react-leaflet';
import {Icon} from "leaflet";
import { css } from "@emotion/core";
import PulseLoader from "react-spinners/PulseLoader";
import axios from 'axios';

const override = css`
  display: block;
  margin-top: 200px;
  border-color: green;
`;

var flag = 0;
var flag2 = 0;
class App extends React.Component {

  state = {
    'location': null,
    'apiKey': 'BDKJluIkcQa-Lwn_Ye9BfW_m8ajO-agWP-WXdpyAMJ3O6iAhangiPCn8Sjch8MF2mikafe4gxR1xxM0h69cCAYBuFlTn0tOvHc2vpiogz3TAHkaRGDeZVRXf46i9XnYx',
    'lat': 0,
    'long': 0,
    'map': null,
    'active': null,
    'isActive': false,
    'isgood': false,
    'popup': null,
    'popPosition': null,
    'loading': false,
    'food_type': false,
    'data2' : null,
    'pickupChecked': false,

  }



handleFoodSearch(e){
  flag2++
  if(flag2 > 4){
  var my_data;
  my_data = this.state.data.filter(biz => {
    if(biz.categories[0].alias.includes(e.target.value)){
      return biz;
    }

  });

  this.setState({food_type: true, data2: my_data});
}

}

handleCheck(e){
  this.setState({pickupChecked: true})
}

  handleChange(e){
    this.setState({loading: true});
    flag++;
    if(flag % 5 == 0){
    axios.get('https://cors-anywhere.herokuapp.com/'+'https://api.yelp.com/v3/businesses/search', {
     params: {
      location: this.state.location,
      limit: 40,
     },
     headers: {
    Authorization: 'Bearer ' + this.state.apiKey
 }
    })
    .then( res => {
      if(!this.state.pickupChecked){

      res.data = res.data.businesses.filter((biz) => {
        if(biz.rating >= 4){
            return biz;
          }
        // if(biz.transactions[0] == 'delivery' ||
        //   biz.transactions[1] == 'delivery'){
        //   return biz
        // }

      })
    }
      this.setState({data: res.data, isgood: true,
       lat: res.data[0].coordinates.latitude,
        long: res.data[0].coordinates.longitude,
        loading: false
      });

    })
    .catch( err => {
      console.log(err);
    })
  }

  }
  render(){

  return( 
    <div className="App">
    <h1>QuickDeliver</h1>
    <br></br>
    <form onSubmit={this.handleChange.bind(this)}>
    <label>Location</label>
    <input value={this.state.location}/>
    <label>Food Type</label>
    <input value={this.state.food_type} onChange={this.handleFoodSearch.bind(this)}/>
    <label>Pickup
    <input type="checkbox" onChange={this.handleCheck.bind(this)} />
    </label>
    <button type="Submit" value="Submit"></button>
    </form>


    <PulseLoader
          css={override}
          size={25}
          color={"#123abc"}
          loading={this.state.loading}
        />
    {this.state.isgood && (
    <Map center={[this.state.lat, this.state.long]} zoom={12}>
    <TileLayer
    url = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    attribution = '&copy; <a href= "https://osm.org/copyright">OpenStreetMap</a>
    contributors'
    />


        {this.state.food_type && this.state.data2.map((b) => (
          <Marker key={b.id}
      position={[b.coordinates.latitude, b.coordinates.longitude]}
       >
       <Popup  
       position={[b.coordinates.latitude, b.coordinates.longitude]} >
        <div>
      <h2>{b.name}</h2>
      <p>Known for: {b.categories[0].title}</p> 
      <p><a href={b.url}>Yelp Page</a></p>
      <p>Phone: <a href={"tel:" + b.phone}>{b.phone}</a></p> 
      </div>
      </Popup>


      </Marker>))
    ||
      this.state.data.map((b) => (
      <Marker key={b.id}
      position={[b.coordinates.latitude, b.coordinates.longitude]}
       >
       <Popup  
       position={[b.coordinates.latitude, b.coordinates.longitude]} >
        <div>
      <h2>{b.name}</h2>
      <p>Known for: {b.categories[0].title}</p> 
      <p><a href={b.url}>Yelp Page</a></p>
      <p>Phone: <a href={"tel:" + b.phone}>{b.phone}</a></p> 
      </div>
      </Popup>


      </Marker>

      ))}

    </Map>
    )}

  </div>
  );
}
}

export default App;
