import React from 'react';
import Menu from './Menu.js'
import History from './History.js'
import PulseLoader from "react-spinners/PulseLoader";
import Slider from 'react-animated-slider';
import { css } from "@emotion/core";
import 'react-animated-slider/build/horizontal.css';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";

const override = css`
  display: block;
  margin-top: 200px;
  border-color: green;
`;
   class Main extends React.Component{

  state = {
     'user_lat': null,
    'user_long': null,
    'comp': null,
    'isloaded': null,
    'redir': null,
    'history': null,
    'menu': null
  }

componentDidMount(){
  navigator.geolocation.getCurrentPosition( (position) => {
      this.setState({user_lat: position.coords.latitude,
        user_long: position.coords.longitude, isloaded: true});
      console.log(this.state.user_long);
  });
}




  handleComp(e){
    e.preventDefault();
    this.setState({comp: true});

  }

  reset(){
    navigator.geolocation.getCurrentPosition( (position) => {
      this.setState({user_lat: position.coords.latitude,
        user_long: position.coords.longitude});
  });
  }

redirect(e) {

  if(e.value == 'History'){
    this.setState({comp: false, isloaded: false, history:true});

  }

  if(e.value == 'Map'){
    this.setState({isloaded: true, history:false});

  }


}

    render(){
  return( 
    <div>
    <Dropdown onChange={this.redirect.bind(this)} options={['Map', 'History', 'Friends']}/>
    <h1 align="left">fooD</h1>
    {!this.state.isloaded &&
    <PulseLoader
          css={override}
          size={25}
          color={"#123abc"}
          loading={this.props.isloaded}
        />
      }
    {this.state.isloaded &&  <button onClick={this.handleComp.bind(this)}>Search Near Me</button>}
    <br></br>
    {this.state.comp && (
      <Menu user_loc={[this.state.user_lat, this.state.user_long]} userFind={true}
      reset={this.reset} />

    )}

    {this.state.history &&

      <Router>
      <Switch>
      <Route path="/history">
      <History />
    </Route>
    </Switch>
    <Redirect to='/history' />

    </Router>
    }

    </div>
    )
}

  }

  export default Main
