import React from 'react';
import Search from './Search.js'
import axios from 'axios';

class Menu extends React.Component {

state = {
    'apiKey': 'BDKJluIkcQa-Lwn_Ye9BfW_m8ajO-agWP-WXdpyAMJ3O6iAhangiPCn8Sjch8MF2mikafe4gxR1xxM0h69cCAYBuFlTn0tOvHc2vpiogz3TAHkaRGDeZVRXf46i9XnYx',
    'map': null,
    'active': null,
    'isActive': false,
    'isgood': false,
    'popup': null,
    'popPosition': null,
    'loading': false,
    'data2' : null,
    'pickupChecked': false,
    'popup': true,

	 'food_type': null,
	 'location': null,
	 'food_filter': false,
}

componentDidMount(){
	this.apiCall(this.props.user_lat, this.props.user_long);
}

handleChange(e){
  this.setState({location: e.target.value});
}

handleFoodSearch(e){
   var my_data;
  my_data = this.state.data.filter(biz => {
    if(biz.categories[0].alias.includes(e.target.value)){
      return biz;
    }

  });

this.setState({food_type: e.target.value, food_filter:true, data2: my_data});

}

 handleSubmit(e){
    e.preventDefault();
    this.setState({loading: true});
    this.apiCall(null,null);
  }

apiCall = (lat, long) => {
  var lim = 10;
  axios.get('https://cors-anywhere.herokuapp.com/'+'https://api.yelp.com/v3/businesses/search', {
     params: {
      location: this.state.location,
      latitude: lat,
      longitude: long,
      limit: lim,
     },
     headers: {
    Authorization: 'Bearer ' + this.state.apiKey
 }
    })
    .then( res => {
      console.log(res.data);
       if(lat == null && long == null){
  		lat = res.data.businesses[0].coordinates.latitude;
  		long = res.data.businesses[0].coordinates.longitude;
  }
      if(!this.state.pickupChecked){

      res.data = res.data.businesses.filter((biz) => {
        if(this.state.food_filter){
        	if(biz.rating >= 4 && biz.categories[0].alias.includes(this.state.food_type)){

            return biz;
          }
      }
      	else{
      		if(biz.rating >= 4){
      			return biz;
      		}

      	}

      })
    }
    console.log('thru');
      this.setState({data: res.data, data2: res.data, isgood: true,
        viewport: {center: [lat, long],
          zoom: 15},
        loading: false
      });


    })
    .catch( err => {
      console.log(err);
    })

}

render(){
	return(
	<div>
	<form onSubmit={this.handleSubmit.bind(this)}>
    <label>Other Location</label>
    <input type="text" value={this.state.location} onChange={this.handleChange.bind(this)}/>
    <input type="submit" value="Submit"/>
    </form>
    <br></br>
    <label>Food Type</label>
    <input type="text" value={this.state.food_type} onChange={this.handleFoodSearch.bind(this)}/>)

	{this.state.isgood && (
	<Search food_type={this.state.food_type}
	data={this.state.data} data2={this.state.data2}
	isgood={this.state.isgood} apiCall={this.apiCall} viewport={this.state.viewport}
	 />
	)}

	</div>
	);

}

}

export default Menu
