import React from 'react';
import Search from './Search.js'
import axios from 'axios';
import {apiCall} from './utils.js'

class Menu extends React.Component {

state = {
    'apiKey': 'BDKJluIkcQa-Lwn_Ye9BfW_m8ajO-agWP-WXdpyAMJ3O6iAhangiPCn8Sjch8MF2mikafe4gxR1xxM0h69cCAYBuFlTn0tOvHc2vpiogz3TAHkaRGDeZVRXf46i9XnYx',
    'active': null,
    'isActive': false,
    'loading': false,
    'data2' : null,
    'pickupChecked': false,
    'popup': true,
    'isgood': false,
	 'food_type': null,
	 'location': null,
	 'food_filter': false,
	 'user_loc': [0,0],
	 'viewport': {center: [0,0], zoom: 15},
	 'data': null,
}

componentDidMount(){
	var send = {location: null, lat: this.props.user_loc[0], long: this.props.user_loc[1]};

	axios.post('http://localhost:3001/apiCall', send)
	.then( (res) => {
		var r = res.data
		this.setState({data: r.data, data2: r.data, isgood: r.isgood, viewport: r.viewport, loading: r.isloading});
	})
	.catch( (err) => {
		console.log(err);
	})


}


handleChange(e){
  this.setState({location: e.target.value});
}

handleFoodSearch(e){
	this.setState({food_filter: false});
   var my_data;
  my_data = this.state.data.filter(biz => {
  	var i;
  	for(i=0; i < biz.categories.length; i++){
  		var item = biz.categories[i].title.toUpperCase()
  		if(item.includes(e.target.value.toUpperCase())){
      return biz;
    }
  	}


  });

this.setState({food_type: e.target.value, food_filter:true, data2: my_data});

}

 handleSubmit(e){
    e.preventDefault();
    this.setState({loading: true});
  	apiCall(this.state.location, null,null);
  }


render(){
	return(
	<div>
	<br></br>
	<form onSubmit={this.handleSubmit.bind(this)}>
    <label>Other Location</label>
    <input type="text" value={this.state.location} onChange={this.handleChange.bind(this)}/>
    <input type="submit" value="Submit"/>
    </form>
    <br></br>
    <label>Food Type</label>
    <input type="text" value={this.state.food_type} onChange={this.handleFoodSearch.bind(this)}/>

	{this.state.isgood && (
	<Search food_type={this.state.food_type}
	data={this.state.data} data2={this.state.data2}
	isgood={this.state.isgood} apiCall={this.apiCall}
	viewport={this.state.viewport}
	recurse={this.props.reset} />

	)}

	</div>
	);

}

}

export default Menu
