import React from 'react';
import logo from './logo.svg';
import './App.css';
import Main from './components/Main.js'
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import axios from 'axios';


class App extends React.Component  {

state = {
  'name': null,
  'email': null,
  'success': null
}

createUser(e){
  e.preventDefault();
  var data = {name: this.state.name, email: this.state.email};
  axios.post('http://localhost:3001/createUser', data)
  .then( (res) => {
    console.log(res.data);
    this.setState({'success': res.data.success});
  })
  .catch( (err) => {
    console.log(err);
  })
}

setName(e){
  this.setState({name: e.target.value})
}

setEmail(e) {
  this.setState({email: e.target.value})
}
  //<Dropdown onChange={} options={['Log in', 'Create account', 'History']}/>

  render(){
  return( 
    <div className="App">
      <h1 align="left">QuickDeliver</h1>
    <h2 align="center">Create Account:</h2>
    <form onSubmit={this.createUser.bind(this)}>
    <label>Name</label>
    <input type="text" value={this.state.name} onChange={this.setName.bind(this)} />
    <label>Email address</label>
    <input type="text" value={this.state.email} onChange={this.setEmail.bind(this)} />
    <input type="submit" value="Submit" />
    <Main isloaded={true} />
    </form>

  </div>
  );
}
}

export default App;
