import React from 'react';
import logo from './logo.svg';
import './App.css';
import Menu from './components/Menu.js'


class App extends React.Component  {
  state = {
     'user_lat': null,
    'user_long': null,
    'comp': null
  }

componentDidMount(){
  navigator.geolocation.getCurrentPosition( (position) => {
      this.setState({user_lat: position.coords.latitude,
        user_long: position.coords.longitude});
      console.log(this.state.user_long);
  });
}

componentDidUpdate(){
  if(!this.state.comp){
    this.setState({comp: true});
  }
}


  handleComp(){
    this.setState({comp: true});

  }

  reset(){
    this.setState({comp: false});
    navigator.geolocation.getCurrentPosition( (position) => {
      this.setState({user_lat: position.coords.latitude,
        user_long: position.coords.longitude});
      console.log(this.state.user_long);
  });

  }

  render(){
  return( 
    <div className="App">
    <h1>QuickDeliver</h1>
    <br></br>
    <button onClick={this.handleComp.bind(this)}>Search Near Me</button>
    <br></br>
    {this.state.comp && (
      <Menu user_lat={this.state.user_lat}
    user_long={this.state.user_long} userFind={true}
    reset={this.reset} />

    )}


  </div>
  );
}
}

export default App;
