import React from 'react';
import logo from './logo.svg';
import './App.css';
import {Map, Marker, Popup, TileLayer} from 'react-leaflet';
import {Icon} from "leaflet";
import { css } from "@emotion/core";
import PulseLoader from "react-spinners/PulseLoader";
import axios from 'axios';

const override = css`
  display: block;
  margin-top: 200px;
  border-color: green;
`;

var end = 0;
class App extends React.Component {
  state = {
    'location': null,
    'apiKey': 'BDKJluIkcQa-Lwn_Ye9BfW_m8ajO-agWP-WXdpyAMJ3O6iAhangiPCn8Sjch8MF2mikafe4gxR1xxM0h69cCAYBuFlTn0tOvHc2vpiogz3TAHkaRGDeZVRXf46i9XnYx',
    'lat': 0,
    'long': 0,
    'map': null,
    'active': null,
    'isActive': false,
    'isgood': false,
    'popup': null,
    'popPosition': null,
    'loading': false,

  }


handlePopup(e){
  console.log(e.latlng);
  this.setState({popPosition: e.latlng});
}

// handleChange(e){
//   axios.get('https://cors-anywhere.herokuapp.com/'+'https://api.yelp.com/v3/businesses/search', {
//     params: {
//       term: e.target.value
//     },
//     headers: {
//     Authorization: 'Bearer ' + this.state.apiKey
//  }
//   }
// )
//   .then( res => {
//     console.log(res.data);
//   })

//   this.setState({location:  e.target.value});

// }

  handleChange(e){
    e.preventDefault();
    this.setState({loading: true});
    axios.get('https://cors-anywhere.herokuapp.com/'+'https://api.yelp.com/v3/businesses/search', {
     params: {
      location: e.target.value,
     },
     headers: {
    Authorization: 'Bearer ' + this.state.apiKey
 }
    })
    .then( res => {
      console.log(res.data);
      res.data = res.data.businesses.filter((biz) => {
        if(biz.transactions[0] == 'delivery' ||
          biz.transactions[1] == 'delivery'){
          return biz
        }

      })
       console.log(res.data);
      this.setState({data: res.data, isgood: true,
       lat: res.data[0].coordinates.latitude,
        long: res.data[0].coordinates.longitude,
        loading: false
      });

    })
    .catch( err => {
      console.log(err);
    })

  }
  render(){

  return( 
    <div className="App">
    <h1>QuickDeliver</h1>
    <br></br>
    <form >
    <input value={this.state.location} onChange={this.handleChange.bind(this)}/>
    <input type='submit' value = 'Submit'/>
    </form>

    <PulseLoader
          css={override}
          size={25}
          color={"#123abc"}
          loading={this.state.loading}
        />
    {this.state.isgood && (
    <Map center={[this.state.lat, this.state.long]} zoom={12}>
    <TileLayer
    url = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    attribution = '&copy; <a href= "https://osm.org/copyright">OpenStreetMap</a>
    contributors'
    />

    {this.state.data.map((b) => (


      <Marker key={b.id}
      position={[b.coordinates.latitude, b.coordinates.longitude]}
       >
       <Popup  
       position={[b.coordinates.latitude, b.coordinates.longitude]} >
        <div>
      <h2>{b.name}</h2>
      <p>Known for: {b.categories[0].title}</p> 
      <p><a href={b.url}>Yelp Page</a></p>
      <p>Phone: <a href={"tel:" + b.phone}>{b.phone}</a></p> 
      </div>
      </Popup>


      </Marker>

      ))}


    {this.state.isActive && 
 <Popup
        key = {this.state.active.id}
      location = {this.state.popPosition}
      >
      <div>
      <h2>{this.state.active.name}</h2>
      <p>Fuck you</p> 
      </div>
      </Popup>
    }
    </Map>
    )}

  </div>
  );
}
}

export default App;
