import React from 'react';
import {Map, Marker, Popup, TileLayer, type Viewport} from 'react-leaflet';
import {Icon} from "leaflet";
import axios from 'axios';
import { css } from "@emotion/core";
import Control from 'react-leaflet-control';
import PulseLoader from "react-spinners/PulseLoader";
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';
import {apiCall} from './utils.js'

const override = css`
  display: block;
  margin-top: 200px;
  border-color: green;
`;
var myLat0 = 0;
var myLong0 = 0;
class Search extends React.Component<{},{ viewport: Viewport },> {
	state = {
    'apiKey': 'BDKJluIkcQa-Lwn_Ye9BfW_m8ajO-agWP-WXdpyAMJ3O6iAhangiPCn8Sjch8MF2mikafe4gxR1xxM0h69cCAYBuFlTn0tOvHc2vpiogz3TAHkaRGDeZVRXf46i9XnYx',
    'map': null,
    'active': null,
    'isActive': false,
    'isgood': false,
    'popup': null,
    'popPosition': null,
    'loading': false,
    'data2' : null,
    'pickupChecked': false,
    'viewport': {center: [0, 0], zoom: 14},
    'popup': true,
    'myLat': null,
    'myLong': null,
    'data': null,
    'data2': null



  }
  componentDidMount(){
  	this.setState({data: this.props.data, data2: this.props.data2});
  }

  onGo(e){
  	console.log(e.target.getCenter());
  }

  updateViewport(e){
  myLat0 = e.center[0];
  myLong0 = e.center[1];

}


prepare(){
	var send = {location: null, lat: myLat0, long: myLong0};

  axios.post('http://localhost:3001/apiCall', send)
  .then( (res) => {
    var r = res.data;
    console.log(r);
    this.setState({data: r.data, data2: r.data, isgood: r.isgood, viewport: r.viewport, loading: r.isloading});
  })
  .catch( (err) => {
    console.log(err);
  })
}

	render() {
		return (
			<div>

			<PulseLoader
          css={override}
          size={25}
          color={"#123abc"}
          loading={this.state.loading}
        />
    {this.props.isgood && (
    <Map
    onViewportChanged={this.updateViewport.bind(this)}
    viewport = {this.props.viewport}
    >
    <TileLayer
    url = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    attribution = '&copy; <a href= "https://osm.org/copyright">OpenStreetMap</a>
    contributors'
    />

    <Control position="topleft">
    <button onClick={this.prepare.bind(this)}> Search Area</button>
    <button onClick={this.props.recurse.bind(this)}>Reset and search near me</button>
    </Control>



        {this.props.food_type && this.state.data2.map((b) => (
          <Marker key={b.id}
      position={[b.coordinates.latitude, b.coordinates.longitude]}
       >
       <Popup onClick={this.setPopup} 
       position={[b.coordinates.latitude, b.coordinates.longitude]} >
        <div>
      <h2>{b.name}</h2>
      <img src={b.image_url} height="150px" width="200px"/>
      <p>Rating: {b.rating}</p>
       <p>Known for:</p>
      <ul>
      {b.categories.map((i) => (
     	<li>{i.title}</li>
  ))}
   		</ul>
      <p><a href={b.url}>Yelp Page</a></p>
      <p>Phone: <a href={"tel:" + b.phone}>{b.phone}</a></p> 

      </div>
      </Popup>


      </Marker>))
    ||
     this.state.data.map((b) => (
      <Marker key={b.id}
      position={[b.coordinates.latitude, b.coordinates.longitude]}
       >
       <Popup  maxWidth="200" maxHeight="200" onClick={this.setPopup}
       position={[b.coordinates.latitude, b.coordinates.longitude]} >
        <div>
      <h2>{b.name}</h2>
      <img src={b.image_url} height="150px" width="200px"/>
      <p>Rating: {b.rating}</p>
      <ul>
      {b.categories.map((i) => (
     	<li>{i.title}</li>
  ))}
   		</ul>
      <p><a href={b.url}>Yelp Page</a></p>
      <p>Phone: <a href={"tel:" + b.phone}>{b.phone}</a></p> 
      </div>
      </Popup>


      </Marker>

      ))}

    </Map>
    )}
    </div>
    )
	}
}

export default Search
