import React from 'react';
import logo from './logo.svg';
import './App.css';
import {Map, Marker, Popup, TileLayer, type Viewport} from 'react-leaflet';
import {Icon} from "leaflet";
import { css } from "@emotion/core";
import Control from 'react-leaflet-control';
import PulseLoader from "react-spinners/PulseLoader";
import axios from 'axios';
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';


const override = css`
  display: block;
  margin-top: 200px;
  border-color: green;
`;

var flag = 0;
var flag2 = 0;
var myLat = 0;
var myLong = 0;
const DEFAULT_VIEWPORT = {
  center: [0, 0],
  zoom: 14,
}
class App extends React.Component<{},{ viewport: Viewport },>  {
  state = {
    'location': null,
    'apiKey': 'BDKJluIkcQa-Lwn_Ye9BfW_m8ajO-agWP-WXdpyAMJ3O6iAhangiPCn8Sjch8MF2mikafe4gxR1xxM0h69cCAYBuFlTn0tOvHc2vpiogz3TAHkaRGDeZVRXf46i9XnYx',
    'lat': 0,
    'long': 0,
    'map': null,
    'active': null,
    'isActive': false,
    'isgood': false,
    'popup': null,
    'popPosition': null,
    'loading': false,
    'food_type': null,
    'data2' : null,
    'pickupChecked': false,
    'viewport': DEFAULT_VIEWPORT,
    'popup': true,
    'user_lat': null,
    'user_long': null,

  }

componentDidUpdate(){
  navigator.geolocation.getCurrentPosition( (position) => {
      this.setState({user_lat: position.coords.latitude,
        user_long: position.coords.longitude});
      console.log(this.state.user_lat);
  });
}

updateViewport = (e) => {
  myLat = e.center[0]
  myLong = e.center[1]
}

handleFoodSearch(e){
  var my_data;
  my_data = this.state.data.filter(biz => {
    if(biz.categories[0].alias.includes(e.target.value)){
      return biz;
    }

  });
  this.setState({food_type: e.target.value, data2: my_data});

}

handleCheck(e){
  this.setState({pickupChecked: true})
}

handleChange(e){
  this.setState({location: e.target.value});
}

apiCall = (e) => {

  axios.get('https://cors-anywhere.herokuapp.com/'+'https://api.yelp.com/v3/businesses/search', {
     params: {
      location: this.state.location,
      latitude: myLat,
      longitude: myLong,
      limit: 20,
     },
     headers: {
    Authorization: 'Bearer ' + this.state.apiKey
 }
    })
    .then( res => {
      console.log(res.data);
      if(!this.state.pickupChecked){

      res.data = res.data.businesses.filter((biz) => {
        if(biz.rating >= 4){
            return biz;
          }

      })
    }
      this.setState({data: res.data, data2: res.data, isgood: true,
        viewport: {center: [res.data[0].coordinates.latitude,
          res.data[0].coordinates.longitude],
          zoom: 15},
        loading: false
      });
      console.log(this.state.viewport);

    })
    .catch( err => {
      console.log(err);
    })

}

  handleSubmit(e){
    e.preventDefault();
    this.setState({loading: true});
    this.apiCall(null,null);
  }

  render(){
  return( 
    <div className="App">
    <h1>QuickDeliver</h1>
    <br></br>
    <form onSubmit={this.handleSubmit.bind(this)}>
    <label>Location</label>
    <input type="text" value={this.state.location} onChange={this.handleChange.bind(this)}/>
    <input type="submit" value="Submit"/>
    </form>
    <br></br>
    <label>Food Type</label>
    <input type="text" value={this.state.food_type} onChange={this.handleFoodSearch.bind(this)}/>


    <PulseLoader
          css={override}
          size={25}
          color={"#123abc"}
          loading={this.state.loading}
        />
    {this.state.isgood && (
    <Map
    onClick={this.closePopup}
    onViewportChanged={this.updateViewport.bind(this)}
    center = {[myLat, myLong]}
    viewport = {this.state.viewport}
    >
    <TileLayer
    url = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    attribution = '&copy; <a href= "https://osm.org/copyright">OpenStreetMap</a>
    contributors'
    />

    <Control position="topleft">
    <button 
          onClick={this.apiCall.bind(this)}
        >
        Search Area
        </button>
        </Control>


        {this.state.food_type && this.state.data2.map((b) => (
          <Marker key={b.id}
      position={[b.coordinates.latitude, b.coordinates.longitude]}
       >
       <Popup onClick={this.setPopup} 
       position={[b.coordinates.latitude, b.coordinates.longitude]} >
        <div>
      <h2>{b.name}</h2>
      <img src={b.image_url} height="150px" width="200px"/>
      <p>Rating: {b.rating}</p>
      <p>Known for: {b.categories[0].title}</p> 
      <p><a href={b.url}>Yelp Page</a></p>
      <p>Phone: <a href={"tel:" + b.phone}>{b.phone}</a></p> 

      </div>
      </Popup>


      </Marker>))
    ||
     this.state.data.map((b) => (
      <Marker key={b.id}
      position={[b.coordinates.latitude, b.coordinates.longitude]}
       >
       <Popup  onClick={this.setPopup}
       position={[b.coordinates.latitude, b.coordinates.longitude]} >
        <div>
      <h2>{b.name}</h2>
      <img src={b.image_url} height="150px" width="200px"/>
      <p>Rating: {b.rating}</p>
      <p>Known for: {b.categories[0].title}</p> 
      <p><a href={b.url}>Yelp Page</a></p>
      <p>Phone: <a href={"tel:" + b.phone}>{b.phone}</a></p> 
      </div>
      </Popup>


      </Marker>

      ))}

    </Map>
    )}

  </div>
  );
}
}

export default App;
