import React from 'react';
import {Map, Marker, Popup, TileLayer, type Viewport} from 'react-leaflet';
import {Icon} from "leaflet";
import axios from 'axios';
import { css } from "@emotion/core";
import Control from 'react-leaflet-control';
import PulseLoader from "react-spinners/PulseLoader";
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';

const override = css`
  display: block;
  margin-top: 200px;
  border-color: green;
`;
var myLat = 0;
var myLong = 0;
class Search extends React.Component<{},{ viewport: Viewport },> {
	state = {
    'apiKey': 'BDKJluIkcQa-Lwn_Ye9BfW_m8ajO-agWP-WXdpyAMJ3O6iAhangiPCn8Sjch8MF2mikafe4gxR1xxM0h69cCAYBuFlTn0tOvHc2vpiogz3TAHkaRGDeZVRXf46i9XnYx',
    'lat': 0,
    'long': 0,
    'map': null,
    'active': null,
    'isActive': false,
    'isgood': false,
    'popup': null,
    'popPosition': null,
    'loading': false,
    'data2' : null,
    'pickupChecked': false,
    'viewport': {center: [0, 0], zoom: 14},
    'popup': true,
    'myLat': 0,
    'myLong': 0,



  }
  componentDidMount(){
  	console.log(this.props.user);
  }

  onGo(e){
  	console.log(e.target.getCenter());
  }

  updateViewport(e){
  myLat = e.center[0];
  myLong = e.center[1];
  console.log(myLat);

}

prepare(){
	this.setState({myLat: myLat, myLong: myLong});
	this.props.apiCall.bind(this, this.state.myLat, this.state.myLong);
}

	render() {
		return (
			<div>

			<PulseLoader
          css={override}
          size={25}
          color={"#123abc"}
          loading={this.state.loading}
        />
    {this.props.isgood && (
    <Map
    onViewportChanged={this.updateViewport.bind(this)}
    viewport = {this.props.viewport}
    >
    <TileLayer
    url = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    attribution = '&copy; <a href= "https://osm.org/copyright">OpenStreetMap</a>
    contributors'
    />

    <Control position="topleft">
    <button 
          onClick={
          	this.prepare}
        >
        Search Area
        </button>
        </Control>


        {this.props.food_type && this.props.data2.map((b) => (
          <Marker key={b.id}
      position={[b.coordinates.latitude, b.coordinates.longitude]}
       >
       <Popup onClick={this.setPopup} 
       position={[b.coordinates.latitude, b.coordinates.longitude]} >
        <div>
      <h2>{b.name}</h2>
      <img src={b.image_url} height="150px" width="200px"/>
      <p>Rating: {b.rating}</p>
      <p>Known for: {b.categories[0].title}</p> 
      <p><a href={b.url}>Yelp Page</a></p>
      <p>Phone: <a href={"tel:" + b.phone}>{b.phone}</a></p> 

      </div>
      </Popup>


      </Marker>))
    ||
     this.props.data.map((b) => (
      <Marker key={b.id}
      position={[b.coordinates.latitude, b.coordinates.longitude]}
       >
       <Popup  onClick={this.setPopup}
       position={[b.coordinates.latitude, b.coordinates.longitude]} >
        <div>
      <h2>{b.name}</h2>
      <img src={b.image_url} height="150px" width="200px"/>
      <p>Rating: {b.rating}</p>
      <p>Known for: {b.categories[0].title}</p> 
      <p><a href={b.url}>Yelp Page</a></p>
      <p>Phone: <a href={"tel:" + b.phone}>{b.phone}</a></p> 
      </div>
      </Popup>


      </Marker>

      ))}

    </Map>
    )}
    </div>
    )
	}
}

export default Search
