import React from 'react';
import logo from './logo.svg';
import './App.css';
import Menu from './components/Menu.js'
import {apiCall} from './components/utils.js'
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import PulseLoader from "react-spinners/PulseLoader";
import Slider from 'react-animated-slider';
import { css } from "@emotion/core";
import 'react-animated-slider/build/horizontal.css';


const override = css`
  display: block;
  margin-top: 200px;
  border-color: green;
`;

class App extends React.Component  {
  state = {
     'user_lat': null,
    'user_long': null,
    'comp': null,
    'isloaded': null
  }

componentDidMount(){
  navigator.geolocation.getCurrentPosition( (position) => {
      this.setState({user_lat: position.coords.latitude,
        user_long: position.coords.longitude, isloaded: true});
      console.log(this.state.user_long);
  });
}




  handleComp(){
    this.setState({comp: true});

  }

  reset(){
    navigator.geolocation.getCurrentPosition( (position) => {
      this.setState({user_lat: position.coords.latitude,
        user_long: position.coords.longitude});
  });
  }

  try_something(e){
    console.log(e);
  }

  render(){
  return( 
    <div className="App">
      <h1 align="left">QuickDeliver</h1>
    <Dropdown onChange={this.try_something.bind(this)} options={['Log in', 'Create account', 'History']}/>
    <br></br>
    {!this.state.isloaded &&
    <PulseLoader
          css={override}
          size={25}
          color={"#123abc"}
          loading={this.state.loading}
        />
      }
    {this.state.isloaded &&  <button onClick={this.handleComp.bind(this)}>Search Near Me</button>}
    <br></br>
    {this.state.comp && (
      <Menu user_loc={[this.state.user_lat, this.state.user_long]} userFind={true}
      reset={this.reset} />

    )}


  </div>
  );
}
}

export default App;
